import React, { useState } from 'react'

const AddCart = (props) => {
    var newArr=[],cartArr=[]
    newArr =props.arr
    const [cart,setCart]=useState(cartArr)
    const btnCart=(event)=>{
        var ind = event.target.getAttribute('index')
        if(ind==='0'){
            var obj = newArr.splice(ind,1)
            cartArr.push(obj)
        }
        setCart([...cartArr])
    }
  return (
    <>
    <div class='product' id='productid' index='1'>
        <img src={props.image} alt=''/>
        <span>
        <h3>{props.name}</h3>
        </span>
        <button className='btnCart' onClick={btnCart} index={props.ind}>Add to Cart</button>
    </div>
    <div id="cart">
        {cart.map((item)=>{
            <table id='tbl'>
                <tr>
                    <td>{item.name}</td>
                    <td>{item.price}</td>
                    <input className='num' type='number'/>
                    <td id='totalId'>{}</td>
                    <td><button id='btnDel'>X</button></td>
                </tr>
            </table>
        })}
    </div>
    </>
  )
}
