import React, { useState } from 'react'
import AddCart from './AddCart'

const HeaderFooter = () => {

  const productsArr = [{name:'Mouse',image:'1.jpeg',price:'100',quantity:''},{name:'Keyboard',image:'2.jpeg',price:'400',quantity:'1'},{name:'Geometry',image:'3.png',price:'50',quantity:'1'}]
  let[cart,setCart]=useState([])
  var index=''
  const btnCart=(event)=>{
    index = parseInt(event.target.getAttribute('index'))
    var val = event.target.getAttribute('val')
    if(cart.length>0){
    for(var i=0;i<cart.length;i++){
      if(cart[i].name===val){
       parseInt(cart[i].quantity++)
       break
      }
      else if(i===cart.length-1){
        cart.push(productsArr[index])
      }
    }
    }
    else{
      cart.push(productsArr[index])
    }
    setCart([...cart])
}
return (
    <>
    <div id='nav'>
      <span>Shopping Cart</span>
    </div>
    <div id='main'>
      {productsArr.map((item,i)=>{
        return (
        <div class='product' id='productid' index='1'>
          <img src={item.image} alt=''/>
          <span>
            <h3>{item.name}</h3>
          </span>
          <button className='btnCart' onClick={btnCart} index={i} val={item.name}>Add to Cart</button>
        </div>
        )
      })}
    </div>
    <AddCart cart={cart}/>
    </>
  )



}

export default HeaderFooter
