import React, { useEffect, useRef, useState } from 'react';
import { Route, Routes } from 'react-router-dom';
import './App.css';
import Category from './Components/Category';
import MainPage from './Components/MainPage';
import Users from './Components/Users';

function App() {
  var searchInp = useRef('');
  const [arr,setArr]=useState([])

  useEffect(()=>{
    if(searchInp.current){
      searchInp.current.focus();
    }
    fetch('https://api.escuelajs.co/api/v1/products')
    .then(res=>res.json())
    .then(data=>{
      data[0].images[0]="https://api.lorem.space/image?w=640&h=480&r=5838"
      setArr(data)
    })
  },[])

  const submit=(e)=>{
    e.preventDefault()
    fetch(`https://openlibrary.org/search.json?q=${searchInp.current.value}&mode=ebooks&has_fulltext=true`)
    .then(res=>res.json())
    .then(data=>{
      setArr(data.docs)
    })
  }

const divHandler=(num)=>{
  fetch('https://openlibrary.org/api/books?bibkeys=ISBN:'+num+'&jscmd=details&format=json')
  .then(res=>res.json())
  .then(data=>{
    let x='ISBN:'+num;
    window.location.href=data[x].info_url
  })
}
  return (
    <div className="App">
    <Routes>
      <Route path='/' element={<MainPage/>}/>
      <Route path='/category' element={<Category/>}/>
      <Route path='/users' element={<Users/>}/>
    </Routes>
    </div>   
  );
}

export default App;
