import React, { useState } from "react"
const AddCart = (props) => {
  let[minus,setminus]=useState('')
  const minusHandler=(event)=>{
    var min = event.target.getAttribute('value')
    min=min--
    console.log(min)

  }
  const plusHandler=(event)=>{
    var max = event.target.getAttribute('value')
    max = max++
    document.getElementById('inp').innerHTML=max
  }
    return (
    <div id="cart">
      <table id='tbl'>
        {props.cart.map((item,i)=>{
            return (
            <tr>
              <td>{item.name}</td>
              <td>{item.price}</td>
              <td>
                <div className="btnDiv">
                  <button onClick={minusHandler} value={item.quantity}>-</button>
                  <input id="inp" className='num' type='number' value={item.quantity}/>
                  <button onClick={plusHandler} value={item.quantity}>+</button>
                </div>
              </td>
              <td><button id='btnDel'>X</button></td>
            </tr>
            )
        })}
        </table>
    </div>
  )
}
export default AddCart
