import React from "react"
const AddCart = (props) => {

    return (
    <div id="cart">
      <table id='tbl'>
        {props.cart.map((item,i)=>{
            return (
              <>
            <tr>
              <td>{item.name}</td>
              <td>{item.price}</td>
              <td>
                <div className="btnDiv">
                  <button onClick={props.minus} quant={item.quantity} ind={i}>-</button>
                  <input id="inp" className='num' type='number' value={item.quantity}/>
                  <button onClick={props.plus} quant={item.quantity} ind={i} >+</button>
                </div>
              </td>
              <td><button id='btnDel' onClick={props.del} ind={i}>X</button></td>
            </tr>
            </>
            )
        })}
        </table>
    </div>
  )
}
export default AddCart
