const AddCart = (props) => {
    var newArr=[],cartArr=[]
    newArr =props.arr
    const [cart,setCart]=useState(cartArr)
    const btnCart=(event)=>{
        var ind = event.target.getAttribute('index')
        if(ind==='0'){
            var obj = newArr.splice(ind,1)
            cartArr.push(obj)
        }
        setCart([...cartArr])
    }
