import React from 'react'
import AddCart from './AddCart'


const HeaderFooter = () => {
  const productsArr = [{name:'Mouse',image:'1.jpeg',price:'100'},{name:'Keyboard',image:'2.jpeg',price:'400'},
{name:'Geometry',image:'3.png',price:'50'},{name:'School bag',image:'4.jpeg',price:'800'}]
  return (
    <>
    <div id='nav'>
      <span>Shopping Cart</span>
      </div>
      <div id='main'>
        {productsArr.map((item,i)=>{
          return (
          <AddCart key={item.name} ind={i} name={item.name} image={item.image} arr={productsArr}/>
          )
        })}
      </div>
    </>
  )
}

export default HeaderFooter
