import React from "react"
const AddCart = (props) => {

  const minusHandler=(event)=>{
    console.log(event.target.next)
  }
    return (
    <div id="cart">
      <table id='tbl'>
        {props.cart.map((item,i)=>{
            return (
            <tr>
              <td>{item.name}</td>
              <td>{item.price}</td>
              <td><div className="btnDiv"><button onClick={minusHandler}>-</button><input className='num' type='number' value={item.quantity}/><button>+</button></div></td>
              <td><button id='btnDel'>X</button></td>
            </tr>
            )
        })}
        </table>
    </div>
  )
}
export default AddCart
