  return (
    <React.Fragment>
        <AppBar>
            <Toolbar>
                <LibraryBooksIcon/>
                <Link to='/'>
                <Typography variant='h4'>Ecommerce Site</Typography>
                </Link>
                <Link to='/category'>Categories</Link>
                <Link to='/users'>Users</Link>
                <Button>Get Started</Button>
            </Toolbar>            
        </AppBar>
        <Box className='search_div'>
            <Typography variant='h4'>Stunning free Books & royalty free stock</Typography>
            <Typography sx={{color:'white'}}>Over 2.7 million+ high quality stock books, videos and music shared by our talented community.</Typography>
            <form onSubmit={props.submit}>
                <input className='search_inp' ref={props.searchInp} placeholder='Search Here...'/>
                <button  style={{display:'none'}} type='submit'/>
            </form> 
            <Typography sx={{marginTop:'20px',color:'white'}}>Popular Books: nature, wallpaper, background, halloween, sky, sky cloud, cat, money, food, dog, flowers, car, iphone</Typography>
        </Box>
        <Box className='list'>
            {props.arr.map((item)=>{
                return(
                    <Box className='item' onClick={()=>props.divHandler(item.isbn[0])}>
                        <Typography className='title'>{item.category.name}</Typography>
                        <img src={item.images[0]} alt="Image not found"/> 
                        <Typography className='content'>${item.price}</Typography>
                        <Typography className='content1'>{item.title}</Typography>
                        <Typography className='content2'>{item.description}</Typography>
