import React from 'react'
import AddCart from './AddCart'

const productsArr = [{name:'Mouse',image:'1.jpeg',price:'100'},{name:'Keyboard',image:'2.jpeg',price:'400'},
{name:'Geometry',image:'3.png',price:'50'},{name:'School bag',image:'4.jpeg',price:'800'}]
const HeaderFooter = () => {
  return (
    <>
    <div id='nav'>
      <span>Shopping Cart</span>
      </div>
      <div id='main'>
        {productsArr.map((item)=>{
          return (<>
          <AddCart ind='1' name={item.name} src={item.image}/>
          <AddCart ind='2' name={item.name} src={item.image}/>
          <AddCart ind='3' name={item.name} src={productsArr[2].image}/>
          <AddCart ind='4' name={productsArr[3].name} src={productsArr[3].image}/>
      </>
          )
        })}


      </div>

    </>
  )
}

export default HeaderFooter
