import React, { useState } from 'react'
import AddCart from './AddCart'


const HeaderFooter = () => {
  const productsArr = [{name:'Mouse',image:'1.jpeg',price:'100'},{name:'Keyboard',image:'2.jpeg',price:'400'},
{name:'Geometry',image:'3.png',price:'50'}]
const[f,setf]=useState(true)
var newArr=[]
const[cart,setCart]=useState(productsArr)
const btnCart=(event)=>{
if(f===true){
  var index = event.target.getAttribute('index')
  if(index==='0'){
    var obj =productsArr.splice(index,1)
    newArr.push(obj)
  }
  setCart([...productsArr])
  setf(false)
}
}
if(f){
  return (
    <>
    <div id='nav'>
      <span>Shopping Cart</span>
    </div>
    <div id='main'>
      {productsArr.map((item,i)=>{
        return (
        <div class='product' id='productid' index='1'>
          <img src={item.image} alt=''/>
          <span>
            <h3>{item.name}</h3>
          </span>
          <button className='btnCart' onClick={btnCart} index={i}>Add to Cart</button>
        </div>
        )
      })}
    </div>

    </>
  )
}
else{
  return 
}

}

export default HeaderFooter
