import React, { useState } from 'react'

const AddCart = (props) => {
    var newArr=[],cartArr=[]
    newArr =props.arr
    const [cart,setCart]=useState(cart)
    const btnCart=(event)=>{
        var ind = event.target.getAttribute('index')
        if(ind==='0'){
            var obj = newArr.splice(ind,1)
            cartArr.push(obj)
        }

    }
  return (
    <>
    <div class='product' id='productid' index='1'>
        <img src={props.src} alt=''/>
        <span>
        <h3>{props.name}</h3>
        </span>
        <button className='btnCart' onClick={btnCart} index={props.ind}>Add to Cart</button>
    </div>
    <div>
        {cartArr.map((item)=>{
            return <>
            {item}
            </>
        })}
    </div>
    </>
  )
}

export default AddCart
