import { AppBar, Toolbar, Typography } from '@mui/material'
import React, { useEffect, useRef, useState } from 'react'
import LibraryBooksIcon from '@mui/icons-material/LibraryBooks';
import { Box } from '@mui/system';

const MainPage = () => {
    var searchInp = useRef('');
    const [arr,setArr]=useState([])

   useEffect(()=>{
    if(searchInp.current){
        searchInp.current.focus();
    }
    fetch('https://openlibrary.org/search.json?q=fantastic+mr+fox&mode=ebooks&has_fulltext=true')
    .then(res=>res.json())
    .then(data=>{
        setArr(data.docs)
        console.log(data.docs)}
    )
   },[])
   const submit=(e)=>{
    e.preventDefault()
    fetch(`https://openlibrary.org/search.json?q=${searchInp.current.value}&mode=ebooks&has_fulltext=true`)
    .then(res=>res.json())
    .then(data=>{
        setArr(data.docs)
    })
}

  return (
    <React.Fragment>
        <AppBar>
            <Toolbar>
                <LibraryBooksIcon/>
                <Typography variant='h4'>Library App</Typography>
            </Toolbar>            
        </AppBar>
        <Box className='search_div'>
            <form onSubmit={submit}>
                <input className='search_inp' ref={searchInp} placeholder='Search Here...'/>
                <button  style={{display:'none'}} type='submit'/>
            </form> 
        </Box>
        <Box className='list'>
            {arr.map((item)=>{
                return(
                    <Box className='item' onclick={divHandler}>
                        <Typography variant='h5'>{item.title}</Typography>
                        <img src={`https://covers.openlibrary.org/b/olid/${item.cover_edition_key}-M.jpg`} alt=""/>
                    </Box>
                )
            })}
        </Box>
    </React.Fragment>
  )
}

export default MainPage
